package loops;

public class while_Dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//while loop
		int k=0;
		while(k<15) {
			System.out.println(k);
		k++;
		}
		
		
	System.out.println("********************");	
		
		
		
		
		
		//do while loop
		int i=0;
		do {
			
			System.out.println(i);
			i++;
		}
		while(i<10);
		
		
		
		
		
	}

}
